import React, { useState } from 'react';
import axios from 'axios';

const EventForm = () => {
    const [event, setEvent] = useState({ name: '', location: '', date: '', description: '', organizerId: '' });

    const handleChange = (e) => {
        setEvent({ ...event, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:8080/api/events', event)
            .then(() => alert('Event created'))
            .catch(err => alert('Error creating event'));
    };

    return (
        <form onSubmit={handleSubmit}>
            <input name="name" placeholder="Event Name" onChange={handleChange} />
            <input name="location" placeholder="Location" onChange={handleChange} />
            <input name="date" type="datetime-local" onChange={handleChange} />
            <input name="description" placeholder="Description" onChange={handleChange} />
            <input name="organizerId" placeholder="Organizer ID" onChange={handleChange} />
            <button type="submit">Create Event</button>
        </form>
    );
};

export default EventForm;
